import { UserNameContext } from "../UsernameContext"
import { useReducer, useContext, useState } from "react"
import { usernameReducer } from "../usernameReducer"
import DataFromLocalStorage from "../DataFromLocalStorage"


const SavedCatThings = () => {

    const { username } = useContext(UserNameContext)
    const [state, dispatch] = useReducer(usernameReducer, username);
    const [thisIsntWorkingButSomePotatoWillHandleThis, setThisIsntWorkingButSomePotatoWillHandleThis]=useState(false);

    return (
        <div>
            <button variant="primary" onClick={() => thisIsntWorkingButSomePotatoWillHandleThis? dispatch({type: 'capitalize' }):dispatch({username: state.username, type: 'capitalize' })}>
                Click here to see your name written with uppercase letters!
            </button>
            <br />
            {state.username}
            <br />
            {localStorage.getItem(username) ? <DataFromLocalStorage /> : "You didn't save any facts yet :/"}
        </div>

    );
}

export default SavedCatThings
