import { useState } from "react"

const AuthorNfo=()=>{
    const [show,setShow]=useState(false);
    const author="Made by Benedek Jakab"
    const place="University of Sopron - Simonyi Karoly Faculty of Engineering, Wood Sciences and Applied Arts" 
    const toShow=()=>{
        <a href="https://github.com/Manage0/web1beadando" ><h1>Source Code available here</h1></a>
            author
            {<br/>}
             
    }

    return(
        <div>
        <button variant="primary" onClick={()=>setShow(!show)}>
        Show author
        </button>
        if (show) {
          <toShow/>
        }    
        </div>)
}

export default AuthorNfo
