import axios from 'axios'
import { useState,useEffect } from 'react'

const RandomCatThings =()=>{

    const [nfo, setNfo]=useState('You will see the facts here!');
    const [facts, setFacts]=useState([]);
    const [loaded, setLoaded]=useState(false);
   
    useEffect(() => {
        const load = async () => {
          try {
            const resp = await axios(
              "https://cat-fact.herokuapp.com/facts"
            )
            setFacts(resp.data)
            setLoaded(true)
          } catch (err) {
            console.log(err)
          }
        }
    
        if (!loaded) {
          load()
        }
      }, [])
      if (!loaded) {
        return("Loading")
      }
    return (
        <div>
            <ul>
                <li>
                    {
                        JSON.stringify(facts.all[0].text)
                    }
                </li>
                <li>
                    {
                        JSON.stringify(facts.all[1].text)
                    }
                </li>
                <li>
                    {
                        JSON.stringify(facts.all[2].text)
                    }
                </li>
                <li>
                    {
                        JSON.stringify(facts.all[3].text)
                    }
                </li>
            </ul>
        </div>
    );
}

export default RandomCatThings
