export function usernameReducer(username, action) {
  switch (action.type) {
    case 'capitalize':
      return {username: username.username.toUpperCase()};
    case 'delete':
      localStorage.removeItem(username.username)
      return {username:""};
    default:
      throw new Error();
  }
}
