import axios from 'axios'
import { useState } from 'react'

const RandomCatThings =()=>{

    const [nfo, setNfo]=useState('You will see the facts here!');
    const [facts, setFacts]=useState([]);

    axios('https://cat-fact.herokuapp.com/facts')
    .then((res) =>{
        for (let index = 0; index < res.data.all.length; index++) {
            setFacts([...facts]+res.data.all[index].text)
        }
    })
    return (
        nfo
    );
}

export default RandomCatThings
