export function usernameReducer(username, action) {
  switch (action.type) {
    case 'capitalize':
      console.log(JSON.stringify(username))
      return {username: username.toUpperCase()};
    case 'delete':
      return {username:""};
    default:
      throw new Error();
  }
}
