import { useState, useEffect } from "react";
import axios from "axios"

const CatPic =()=>{

    const [loaded,setLoaded]=useState(false)
    const [pic,setPic]=useState();
    
    useEffect(() => {
        const load = async () => {
          try {
            const resp = await axios(
              "https://cataas.com/c"
            )
            console.log(resp.data)
            setPic(resp.data)
            setLoaded(true)
          } catch (err) {
            console.log(err)
          }
        }
    
        if (!loaded) {
          load()
        }
      }, [])

    if(!loaded)
    return (
    <div>
        <b>Picture of a cat will be here!</b>
    </div>   
    );

    if (loaded)
    return(
        <div>
            <img src="https://cataas.com/c" width="500" height="600"></img>
        </div>
    );
}

export default CatPic;
